mapper.select:
select sum(total)
from (
         select mem_id, bk_amount*sl.ss_price as total
         from tb_basket bk join tb_sales sl
                                on bk.ss_num = sl.ss_num
         where mem_id = #{mem_id}
     ) AA
group by mem_id

select sum(total)
from (
         select mem_id, sl.ss_price as total
         from tb_basket bk join tb_sales sl
                                on bk.ss_num = sl.ss_num

         where mem_id = #{mem_id}
     ) AA

select bk_num, ss_num, bk_amount, mem_id
from tb_basket
where mem_id = #{mem_id}
