mapper.select:
SELECT gm_code
FROM tb_game
WHERE gm_name = #{gm_name}

SELECT mem_nick
FROM tb_mem
WHERE mem_id = #{mem_id}

SELECT rl_name
FROM tb_role
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT rl_name, rs_seat
FROM tb_roleseat
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT rs_seat
FROM tb_roleseat
WHERE rcrbrd_num = #{rcrbrd_num}
  AND rs_seat = #{rs_seat}

SELECT mem_id, ri_seat, ri_num
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT mem_id, ri_seat
FROM tb_recruitinfo
WHERE rcrbrd_num = #{rcrbrd_num}

SELECT mm.mem_nick
FROM tb_recruitinfo as ri
         JOIN tb_mem as mm
              ON ri.mem_id = mm.mem_id
WHERE ri.rcrbrd_num = #{rcrbrd_num}
  AND ri.ri_seat = #{ri_seat}

SELECT rcrbrd_max
FROM tb_rcrboard
WHERE rcrbrd_num = #{rcrbrd}

SELECT mm.mem_pic, mm.mem_id
FROM tb_recruitinfo as ri
         JOIN tb_mem as mm
              ON ri.mem_id = mm.mem_id
WHERE ri.rcrbrd_num = #{rcrbrd_num}
  AND ri.ri_seat = #{ri_seat}

mapper.update:
UPDATE tb_rcrboard
SET rcrbrd_status = '모집완료'
WHERE rcrbrd_num = #{rcrbrd_num}

UPDATE tb_rcrboard
SET rcrbrd_views = rcrbrd_views + 1
WHERE rcrbrd_num = #{rcrbrd_num}
